import React, { useState } from 'react';
import Regraschat from './Regraschat';
import './Home.css'; // Importe o arquivo de estilo CSS

const Home = () => {
    const [showRegraschat, setShowRegraschat] = useState(true);
    const [mensagemUsuario, setMensagemUsuario] = useState('');
    const [mensagens, setMensagens] = useState([
        { texto: 'Bem vindo! Eu sou Jules, a IA que irÃ¡ te ajudar a falar francÃªs. Para comeÃ§ar, basta digitar a situaÃ§Ã£o que deseja conversar comigo, como por exemplo: Conversas tÃ­picas em uma padaria, restaurantes, festas... sinta-se livre para escolher qualquer coisa', usuario: 'Jules' },
        { texto: 'OlÃ¡ Jules, vamos simular uma conversa onde vocÃª Ã© um padeiro.', usuario: 'UsuÃ¡rio' },
    ]);

    const hideRegraschat = () => {
        setShowRegraschat(false);
    };

    const handleMensagemChange = (e) => {
        setMensagemUsuario(e.target.value);
    };

    const handleEnviarClick = () => {
        const novaMensagem = { texto: mensagemUsuario, usuario: 'UsuÃ¡rio' };
        setMensagens([...mensagens, novaMensagem]);
        setMensagemUsuario('');
    };

    return (
        <>
            <div className="home-container">
                <center>
                    <h1>
                        Aprenda FrancÃªs com IA ðŸ¤–
                    </h1>
                </center>
            </div>
            <img id='orangeguy' src={require('./images/orangeguy.png')} />
            <div className='chat-area'>
                {showRegraschat ? (
                    <Regraschat onClose={hideRegraschat} />
                ) : (
                    <>
                        {mensagens.map((mensagem, index) => (
                            <div key={index} className={`mensagem ${mensagem.usuario === 'Jules' ? 'mensagem-jules' : 'mensagem-usuario'}`}>
                                <p>{mensagem.texto}</p>
                            </div>
                        ))}
                        <div id='campo-texto'>
                            <textarea
                                id='campo-texto-area'
                                value={mensagemUsuario}
                                onChange={handleMensagemChange}
                                placeholder='Digite aqui'
                            />
                            <button id='enviar-button' onClick={handleEnviarClick}>
                                Enviar
                            </button>
                        </div>
                    </>
                )}
            </div>
            <button id='como-funciona' onClick={() => setShowRegraschat(true)}>Como Funciona?</button>
        </>
    );
};

export default Home;
