import React, { useState } from 'react';

const Regraschat = ({ onClose }) => {
  const [showMessage, setShowMessage] = useState(true);

  const hideMessage = () => {
    setShowMessage(false);
    onClose();
  };

  return (
    showMessage && (
      <center>
      <div id='regras-chat'>
        <h2>Regras</h2>
        <p>01.Digite a mensagem no campo de mensagem</p>
        <button onClick={hideMessage}>Fechar</button>
      </div>
      </center>
    )
  );
};

export default Regraschat;
