import React, { useState } from 'react';

const Regraschat = ({ onClose }) => {
  const [showMessage, setShowMessage] = useState(true);

  const hideMessage = () => {
    setShowMessage(false);
    onClose();
  };

  return (
    showMessage && (
      <center>
        <h2>Regras</h2>
      <div id='regras-chat'>
        
        <p>Digite a mensagem no campo de mensagem</p>
        <p>Para enviar a mensagem basta clicar em enviar</p>
        <p>Tente manter a conversa com respeito</p>
        <p>DIVIRTA-SE E BONS ESTUDOS</p>
        <button onClick={hideMessage}>Fechar</button>
      </div>
      </center>
    )
  );
};

export default Regraschat;
