import React, { useState } from 'react';
import Regraschat from './Regraschat';
import './Home.css'; // Importe o arquivo CSS para estilos

const Home = () => {
    const [showRegraschat, setShowRegraschat] = useState(true);
    const [mensagens, setMensagens] = useState([]);
    const [mensagemUsuario, setMensagemUsuario] = useState('');

    const hideRegraschat = () => {
        setShowRegraschat(false);
    };

    const handleMensagemChange = (e) => {
        setMensagemUsuario(e.target.value);
    };

    const handleEnviarClick = () => {
        const novaMensagem = {
            tipo: 'usuario',
            conteudo: mensagemUsuario
        };

        setMensagens([...mensagens, novaMensagem]);
        setMensagemUsuario('');
    };

    return (
        <>
            <div className="home-container">
                <center>
                    <h1>
                        Aprenda FrancÃªs com IA ðŸ¤–
                    </h1>
                </center>
            </div>
            <img id='orangeguy' src={require('./images/orangeguy.png')} />
            <div className='chat-area'>
                {showRegraschat ? (
                    <Regraschat onClose={hideRegraschat} />
                ) : (
                    <>
                        {/* Exibir mensagens anteriores */}
                        {mensagens.map((mensagem, index) => (
                            <div key={index} className={mensagem.tipo === 'usuario' ? 'msg-usuario' : 'msg-resposta'}>
                                <p>{mensagem.conteudo}</p>
                            </div>
                        ))}

                        {/* Caixa de texto para o usuÃ¡rio digitar */}
                        <div id='campo-texto'>
                            <textarea 
                                id='campo-texto-area' 
                                value={mensagemUsuario}
                                onChange={handleMensagemChange}
                                placeholder='Digite aqui'
                            />
                            <button id='enviar-button' onClick={handleEnviarClick}>
                                Enviar
                            </button>
                        </div>
                    </>
                )}
            </div>
            <button id='como-funciona' onClick={() => setShowRegraschat(true)}>Como Funciona?</button>
        </>
    );
};

export default Home;
